function formatsvg(svg: string) {
    return svg.slice(0, 4) + ` xmlns="http://www.w3.org/2000/svg"` + svg.slice(4);
}

export function getStaticPaths() {
    return Object.keys(icons).map(icon => ({ params: { id: icon } }));
}

export const get = ({ params }: { params: { id: keyof typeof icons } }) => {
    return {
        body: formatsvg(icons[params.id])
    }
};